import React, { useState, useEffect } from "react";
import firebase from "../../utils/firebase";
import { useAuth } from "../context/authContext";
import Navbar from "../lobby/navbar";
import { useHistory } from "react-router-dom";
import "./chats.css";
function Chats() {
  const history = useHistory();
  const chats = firebase.firestore().collection("Chat");
  const { currentUser } = useAuth();
  const [chat, setChat] = useState([]);
  useEffect(() => {
    const unsubscriber = chats
      .where("integrantes", "array-contains", currentUser.email)
      .get()
      .then((data) => {
        let Chat = [];
        data.forEach((doc) => {
          Chat.push(doc.id);
        });
        setChat(Chat);
        console.log(Chat);
      });
    return unsubscriber;
  }, [chats, currentUser.email]);
  return (
    <>
      <Navbar />
      <div className="chat-menu fondo">
        {chat.map((id) => {
          console.log("?");
          return (
            <div className="chat-contenedor">
              <div className="chat-info">
                <h1>Chat con Arjona</h1>
                <button
                  className="primario"
                  onClick={() => history.push(`/chat/${id}`)}
                >
                  Ir al chat
                </button>
                <h5>No te lo pierdas</h5>
                <p>Entra ya!</p>
              </div>
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Chats;
